import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Accordion } from '@material-ui/core';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import theme from '../theme/theme';
import service from '../services/CourseService';
import { useEffect } from 'react';

const useStyles = makeStyles(() => ({
  root: {
    maxWidth: '960px',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0,
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
}));

export default function App() {

  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  const [isLoading, setLoading] = React.useState(true);
  const [courses, setCourses] = React.useState([]);

  // useEffect(async () => {
  //   setCourses(service.getCourses());
  // }, [])

  useEffect(() => {
      service.getCourses().then(response => {
        setCourses(response.data);
        setLoading(false);
      })
  }, []);

  console.log("pls" + courses);
  // let coursesJSON;

  // useEffect(() => {

  //   async function getData() {
  //     coursesJSON = await service.getCourses();
  //     return coursesJSON;
  //   }

  //   getData();

  // },[] );

  // coursesJSON = await service.getCourses();
  // console.log("JSON", coursesJSON);
  // const courses = Object.entries(coursesJSON);
  // console.log("entries", courses);
  // courses.forEach(([key, value]) => console.log("HELLO???", key, value));

  const classes = useStyles();

  if(isLoading) {
    return <div>Loading...</div>
  }

  return (

    <div className={classes.root}>
      <Grid container>
        <Grid item xs={12} sm={6}>

        {courses.map(course => (
          <Accordion key={course._id} expanded={expanded === 'panel'} onChange={handleChange('panel')}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls="panelbh-content" id="panelbh-header" >
              <Typography className={classes.heading}>{course.name}</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>{course.description}</Typography>
            </AccordionDetails>
          </Accordion>
        ))}

      </Grid>
      </Grid>
    </div>

  )
}
