import React from 'react';
import { Container, Box, Typography, Grid } from '@material-ui/core';
import Carousel from 'react-material-ui-carousel'
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Biography
 * @Summary This component displays a biography and picture of myself
 */

const useStyles = makeStyles(() => ({
      contentBox: {
        maxWidth: theme.breakpoints.values['md'],
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
        marginLeft: 'auto',
        marginRight: 'auto',
        textAlign: 'left',
        // [theme.breakpoints.up('lg')]: {
        //   maxWidth: theme.breakpoints.values['lg'] / 2,
        //   maxHeight: 512,
        //   paddingTop: theme.spacing(16),
        //   paddingBottom: theme.spacing(16),
        //   marginRight: 0,
        //   textAlign: 'left',
        // }
      },
      img: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        objectFit: 'cover',
        height: 512,
        width: '100%'
      },
      button: {
        color: theme.palette.text.black,
        backgroundColor: "transparent",
        height: "50px",
      },
      halfLg: {
        [theme.breakpoints.up('lg')]: {
          maxWidth: theme.breakpoints.values['lg'] / 2
        }
      },
      firstBox: {
        [theme.breakpoints.up('lg')]: {
          marginLeft: 'auto',
          paddingRight: theme.spacing(6)
        }
      },
      fullHeightImage: {
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        objectFit: 'cover',
        maxHeight: '700',
        width: '100%'
      },
}));

export default function App() {

  const classes = useStyles();


  return (

    <section>

    <Grid container>
    {/* Image that adjusts based on screen size */}
        <Grid item xs={12} md={6}>
          <Box position="relative" maxHeight={700}>
            <Carousel  swipe={true} animation="fade" navButtonsAlwaysInvisible={true} interval="5000">
                <img className={classes.fullHeightImage} src="https://hollandaucoin-images.s3-us-west-1.amazonaws.com/main/AboutMe.jpg" alt="" />
                <img className={classes.fullHeightImage} src="https://hollandaucoin-images.s3-us-west-1.amazonaws.com/main/Camp.jpg" alt="" />
                <img className={classes.fullHeightImage} src="https://hollandaucoin-images.s3-us-west-1.amazonaws.com/main/Soccer.jpg" alt="" />
              </Carousel>
          </Box>
        </Grid>
        <Grid item xs={12} md={6} style={{alignSelf: "center"}}>
          <Box pt={6} pb={6} display="flex" className={[classes.halfLg, classes.firstBox]}>
            <Container>
              <Box mb={4}>
                <Typography variant="h4" component="h2" gutterBottom={true}>Get to know me.</Typography>
                <Typography variant="subtitle1" color="textSecondary" paragraph={true}>
                    Hello, thanks for visiting my portfolio. My name is Holland Aucoin and I am currently a student at Grand Canyon University 
                    in Phoenix, Arizona, pursuing a Bachelor of Science in Computer Programming, scheduled to graduate in April 2021. I have 
                    spent the last several years building full-stack applications using a variety of languages and frameworks, while learning 
                    and practicing agile methodologies in a team-driven environment.
                </Typography>
                <Typography variant="subtitle1" color="textSecondary" paragraph={true}>
                    Aside from that, I am extremely passionate about people, building connections, and servant leadership, with a strong desire 
                    to find a company that has those same values.
                </Typography>
              </Box>
            </Container>
          </Box>
        </Grid>
      </Grid>
    </section>
  )
}
