import React, { useEffect } from 'react';
import { Container, Box, Typography, Grid, Card, CardMedia } from '@material-ui/core';
import service from '../services/SkillService';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Porject Details
 * @Summary This component displays the details of an individual project
 */

const useStyles = makeStyles(() => ({
  icon: {
    marginBottom: theme.spacing(1),
  }
}));

export default function App() {

  // Creating variables of skill lists to be filled be appropriate service call
  const [languageSkills, setLanguages] = React.useState([]);

  // Use useEffect method to call backend for each skill type and wait for response
  useEffect(() => {
    service.getSkillsByType("Languages").then(response => {
      setLanguages(response.data);
    })
  }, []);

  const classes = useStyles();

  return (

      <section>
        <Container maxWidth="md" >
          <Box py={6}>
            <div align="center" style={{paddingBottom: "25px"}}>
              <Typography variant="h5" gutterBottom={true}>Logo</Typography>
            </div>
            <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>Alias sunt voluptas ratione modi dolore nostrum debitis nihil. Nemo, ratione repellat quia doloremque perferendis fuga cumque ex corporis laborum distinctio dolorum deserunt voluptates ea architecto ab, esse omnis quas provident. Maiores sed ipsam eos quis.</Typography>

            <Grid container spacing={4} style={{ justifyContent: "center" }}>
              {/* Iterate through the skills using map, and use index variable to display each icon and title */}
              {languageSkills.map((skill, id) => (
                  <Grid key={skill.name} item xs={6} sm={4} md={2} style={{textAlign: "center"}}>
                    <Card className={classes.cardRoot}>
                      <CardMedia className={classes.cardMedia} component="img" src="" alt="grad image"/>
                    </Card>
                  </Grid>
                ))
              }
            </Grid>
          </Box>
        </Container>
      </section>

  )
}
