import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Grid, Container, Typography, Box, Avatar } from '@material-ui/core';
import service from '../services/RecommendationService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';
import MarkReha from './../images/MarkReha.jpg';
import ShadSluiter from './../images/ShadSluiter.jpg';
import ColeParker from './../images/ColeParker.jpg';
import TimTurner from './../images/TimTurner.jpg';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Recommendations
 * @Summary This component displays recommendations and information about the author
 */

const useStyles = makeStyles(() => ({
    container: {
        justifyContent: 'center'
      },
      avatar: {
        width: theme.spacing(14),
        height: theme.spacing(14),
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: theme.spacing(2)
      },
}));

export default function App() {

    // Creating variable for recommendations
    const [recommendations, setRecommendations] = React.useState([]);

    // Use useEffect method to call backend for course information and wait for response
    useEffect(() => {
        service.getRecommendations().then(response => {
            setRecommendations(response.data);
        })
    }, []);

    const classes = useStyles();

    return (

        <section>
            <Container maxWidth="md">
                <Box pt={8} pb={12} textAlign="center">
                    <Box mb={8}>
                        <Typography variant="h4" component="h2" gutterBottom={true}>The Pipers</Typography>
                        <Typography variant="subtitle1" color="textSecondary">We are the guys that made this whole thing possible.</Typography>
                    </Box>
                    <Grid container spacing={8} className={classes.container}>
                        <Grid item xs={12} sm={3}>
                            <Avatar alt="" src={ MarkReha } className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Mark Reha</Typography>
                            <Typography variant="body1" color="primary" component="span">Professor at Grand Canyon University</Typography>
                        </Grid>
                        <Grid item xs={12} sm={9} style={{textAlign: "left"}}>
                            <Typography variant="p" gutterBottom={true}>words from reha</Typography>
                        </Grid>
                        <Grid item xs={12} sm={9} style={{textAlign: "left"}}>
                            <Typography variant="p" gutterBottom={true}>Richard Hendricks</Typography>
                        </Grid>
                        <Grid item xs={12} sm={3}>
                            <Avatar alt="" src={ TimTurner } className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Tim Turner</Typography>
                            <Typography variant="body1" color="primary" component="span">Assistant Director at Mt. Baker Leadership Camp</Typography>
                        </Grid>
                    </Grid>
                </Box>
            </Container>
        </section>

    )
}
