import React, { useState} from 'react';
// import MediaQuery from 'react-responsive';
import { Container, Box, Typography, Grid, Button, Paper } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Mail, LinkedIn } from '@material-ui/icons';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        justifyContent: 'space-evenly',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(12),
            height: theme.spacing(12),
        },
    },
    button: {
        color: theme.palette.text.black, 
        backgroundColor: theme.palette.background.white, 
        height: "50px"
    }
}));

export default function App() {

    const classes = useStyles();

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [subject, setSubject] = useState('');
    const [message, setMessage] = useState('');

    const onChangeFirstName = (event) => {
      setFirstName(event.target.value);
    }
    const onChangeLastName = (event) => {
      setLastName(event.target.value);
    }
    const onChangeEmail = (event) => {
      setEmail(event.target.value);
    }
    const onChangeSubject= (event) => {
        setSubject(event.target.value);
    }
    const onChangeMessage = (event) => {
        setMessage(event.target.value);
    }

    return (

        <section>
            <Container className="MuiContainer-maxWidthSm !important">
                <Box pt={8} pb={10}>
                    <Box mb={6} textAlign="center">
                        <Typography variant="h4" component="h2" gutterBottom={true}>CONTACT</Typography>
                        <hr width="60px" />
                        <Typography variant="subtitle1" color="textSecondary" paragraph={true}>We're here to answer your questions and discuss the decentralized future of the internet. Let's talk!</Typography>
                        <div className={classes.root}>
                            <Paper elevation={3}>
                                <Button aria-label="LinkedIn" style={{ width: "100%", height: "100%" }} onClick={() => window.open("https://www.linkedin.com/in/hollandaucoin/", "_blank")}>
                                    <LinkedIn style={{ fontSize: 80, fill: "linear-gradient(140deg, #F3B0F2 5%, #DF63D3 15%, #8C53CC 50%, #0A8DC8 75%, #51E2EB 100%)" }} />
                                </Button>
                            </Paper>
                            <Paper elevation={3}>
                                <Button style={{ width: "100%", height: "100%" }} aria-label="Mail" key="Email" component="a" href="mailto:holland.aucoin2@gmail.com">
                                    <Mail style={{ fontSize: 80 }} />
                                </Button>
                            </Paper>
                            <Paper elevation={3}>
                                <Button aria-label="LinkedIn" style={{ width: "100%", height: "100%" }} onClick={() => window.open("https://www.linkedin.com/in/hollandaucoin/", "_blank")}>
                                    <LinkedIn style={{ fontSize: 80 }} />
                                </Button>
                            </Paper>
                        </div>
                    </Box>
                    <Box>
                        <ValidatorForm onError={errors => console.log(errors)}>
                            <Grid container spacing={2}>
                                <Grid item xs={12} sm={6}>
                                <TextValidator variant="outlined" fullWidth
                                    label="First Name *"
                                    onChange={onChangeFirstName}
                                    name="firstName"
                                    value={firstName}
                                    validators={['required', 'matchRegexp:^[a-zA-Z]+$', 'matchRegexp:^.{2,15}$']}
                                    errorMessages={['This field is required', 'Can only contain letters', 'Must be 2 to 15 characters']}
                                />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextValidator variant="outlined" fullWidth
                                        label="Last Name *"
                                        onChange={onChangeLastName}
                                        name="lastName"
                                        value={lastName}
                                        validators={['required', 'matchRegexp:^[a-zA-Z]+$', 'matchRegexp:^.{2,15}$']}
                                        errorMessages={['This field is required', 'Can only contain letters', 'Must be 2 to 15 characters']}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                <TextValidator variant="outlined" fullWidth
                                    label="Email *"
                                    onChange={onChangeEmail}
                                    name="email"
                                    value={email}
                                    validators={['required', 'isEmail']}
                                    errorMessages={['This field is required', 'Invalid email']}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                <TextValidator variant="outlined" fullWidth
                                    label="Subject *"
                                    onChange={onChangeSubject}
                                    name="subject"
                                    value={subject}
                                    validators={['required']}
                                    errorMessages={['This field is required']}
                                />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextValidator variant="outlined" fullWidth multiline rows={5}
                                        label="Message *"
                                        onChange={onChangeMessage}
                                        name="message"
                                        value={message}
                                        validators={['required']}
                                        errorMessages={['This field is required']}
                                    />
                                </Grid>
                            </Grid>
                            <Box my={2}>
                                <Button className={classes.button} type="submit" fullWidth variant="contained">Submit</Button>
                            </Box>
                        </ValidatorForm>
                    </Box>
                </Box>
            </Container>
        </section>

    )
}
