import React, { useEffect } from 'react';
import MediaQuery from 'react-responsive';
import { Link } from 'react-router-dom';
import { Grid, Container, Typography, Box, Avatar, IconButton, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Button } from '@material-ui/core';
import service from '../services/RecommendationService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';
import { Mail, LinkedIn } from '@material-ui/icons';
// import Popup from '../components/Popup';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import pdf from '../images/Tim.pdf';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Recommendations
 * @Summary This component displays recommendations and information about the author
 */

const useStyles = makeStyles(() => ({
    container: {
        justifyContent: 'center'
    },
    avatar: {
        width: theme.spacing(15),
        height: theme.spacing(15),
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: theme.spacing(2)
    },
}));

export default function App() {

    const [open, setOpen] = React.useState(false);
    const fullScreen = useMediaQuery(theme.breakpoints.down('xl'));

    const handleClickOpen = () => {
      setOpen(true);
    };

    const handleClose = () => {
      setOpen(false);
    };

    // Creating variable for recommendations
    const [recommendations, setRecommendations] = React.useState([]);

    // Use useEffect method to call backend for course information and wait for response
    useEffect(() => {
        service.getRecommendations().then(response => {
            setRecommendations(response.data);
        })
    }, []);

    const classes = useStyles();

    return (

        <section>
            <Container maxWidth="md">
                <Box pt={8} pb={12} textAlign="center">
                    <Box mb={8}>
                        <Typography variant="h4" component="h2" gutterBottom={true}>Recommendations</Typography>
                        {/* <Typography variant="subtitle1" color="textSecondary">We are the guys that made this whole thing possible.</Typography> */}
                    </Box>
                    <Grid container spacing={8} className={classes.container}>
                        <MediaQuery minWidth={theme.breakpoints.values.iPad}>
                            {recommendations.map((recommendation, index) => (
                                (() => {

                                    if (index % 2 === 0) {
                                        return ([
                                            <Grid item xs={12} sm={3}>
                                                <Avatar alt="" src={require("../images/" + recommendation.image + ".jpg").default} className={classes.avatar} />
                                                <Typography variant="h6" component="h4" gutterBottom={true}>{recommendation.name}</Typography>
                                                <Typography variant="body1" color="primary" component="span">{recommendation.position}</Typography><br />
                                                <IconButton aria-label="LinkedIn" onClick={() => window.open(recommendation.contact.linkedIn, "_blank")}>
                                                    <LinkedIn />
                                                </IconButton>
                                                <IconButton aria-label="Mail" button="true" key="Email" component="a" href={"mailto:" + recommendation.contact.email}>
                                                    <Mail />
                                                </IconButton>
                                            </Grid>,
                                            <Grid item xs={12} sm={9}>
                                                <Typography gutterBottom={true} style={{ textAlign: "justify" }}>"{recommendation.letter}"</Typography>
                                                <Link to="/" style={{ textDecoration: 'none'}}>See More</Link>
                                                {/* <Popup /> */}
                                                <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open responsive dialog
      </Button>
      <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Let Google help apps determine location. This means sending anonymous location data to
            Google, even when no apps are running.
          </DialogContentText>
          <img alt="" src={pdf} />
        </DialogContent>
      </Dialog>
                                            </Grid>
                                        ])
                                    }
                                    else {
                                        return ([
                                            <Grid item xs={12} sm={9} style={{ textAlign: "justify" }}>
                                                <Typography gutterBottom={true}>"{recommendation.letter}"</Typography>
                                            </Grid>,
                                            <Grid item xs={12} sm={3}>
                                                <Avatar alt="" src={require("../images/" + recommendation.image + ".jpg").default} className={classes.avatar} />
                                                <Typography variant="h6" component="h4" gutterBottom={true}>{recommendation.name}</Typography>
                                                <Typography variant="body1" color="primary" component="span">{recommendation.position}</Typography><br />
                                                <IconButton aria-label="LinkedIn" onClick={() => window.open(recommendation.contact.linkedIn, "_blank")}>
                                                    <LinkedIn />
                                                </IconButton>
                                                <IconButton aria-label="Mail" button="true" key="Email" component="a" href={"mailto:" + recommendation.contact.email}>
                                                    <Mail />
                                                </IconButton>
                                            </Grid>
                                        ])
                                    }
                                })()
                            ))}
                        </MediaQuery>
                        <MediaQuery maxWidth={theme.breakpoints.values.iPad - 1}>
                            {recommendations.map((recommendation) => ([
                                <Grid item xs={12} sm={3}>
                                    <Avatar alt="" src={require("../images/" + recommendation.image + ".jpg").default} className={classes.avatar} />
                                    <Typography variant="h6" component="h4" gutterBottom={true}>{recommendation.name}</Typography>
                                    <Typography variant="body1" color="primary" component="span">{recommendation.position}</Typography><br />
                                    <IconButton aria-label="LinkedIn" onClick={() => window.open(recommendation.contact.linkedIn, "_blank")}>
                                        <LinkedIn />
                                    </IconButton>
                                    <IconButton aria-label="Mail" button="true" key="Email" component="a" href={"mailto:" + recommendation.contact.email}>
                                        <Mail />
                                    </IconButton>
                                </Grid>,
                                <Grid item xs={12} sm={9} style={{ textAlign: "justify" }}>
                                    <Typography gutterBottom={true}>{recommendation.letter}</Typography>
                                </Grid>
                            ]))}
                        </MediaQuery>
                    </Grid>
                </Box>
            </Container>
        </section>

    )
}
