import React, { useEffect } from 'react';
import { Container, Box, Typography, Grid, Paper } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';
import service from '../services/SkillService';
import { Mail, LinkedIn, GitHub } from '@material-ui/icons';

const useStyles = makeStyles(() => ({
  icon: {
    marginBottom: theme.spacing(1),
  }
}));

export default function App() {

  const [languageSkills, setLanguages] = React.useState([]);
  const [IDESkills, setIDEs] = React.useState([]);
  const [toolSkills, setTools] = React.useState([]);
  const [SDSkills, setSD] = React.useState([]);
  const [frameworkSkills, setFrameworks] = React.useState([]);
  const [DPSkills, setDP] = React.useState([]);
  const [agileSkills, setAgile] = React.useState([]);
  const [serverSkills, setServers] = React.useState([]);
  const [cloudSkills, setClouds] = React.useState([]);
  const [DBSkills, setDBs] = React.useState([]);

  useEffect(() => {
    service.getSkillsByType("Languages").then(response => {
      setLanguages(response.data);
    })
    service.getSkillsByType("IDE").then(response => {
      setIDEs(response.data);
    })
    service.getSkillsByType("Tools").then(response => {
      setTools(response.data);
    })
    service.getSkillsByType("Software Design").then(response => {
      setSD(response.data);
    })
    service.getSkillsByType("Web Frameworks").then(response => {
      setFrameworks(response.data);
    })
    service.getSkillsByType("Design Patterns").then(response => {
      setDP(response.data);
    })
    service.getSkillsByType("Agile Scrum").then(response => {
      setAgile(response.data);
    })
    service.getSkillsByType("Application Servers").then(response => {
      setServers(response.data);
    })
    service.getSkillsByType("Cloud Services").then(response => {
      setClouds(response.data);
    })
    service.getSkillsByType("Databases").then(response => {
      setDBs(response.data);
    })
  }, []);

  let languages = "";
  languageSkills.map((language, index) => (
    languages += language + (index < languageSkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  let IDEs = "";
  IDESkills.map((IDE, index) => (
    IDEs += IDE + (index < IDESkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  let tools = "";
  toolSkills.map((tool, index) => (
    tools += tool + (index < toolSkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  let SDs = "";
  SDSkills.map((SD, index) => (
    SDs += SD + (index < SDSkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  let frameworks = "";
  frameworkSkills.map((framework, index) => (
    frameworks += framework + (index < frameworkSkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  let DPs = "";
  DPSkills.map((DP, index) => (
    DPs += DP + (index < DPSkills.length - 1 ? ',\u00A0 ' : ' ')
  ))

  const classes = useStyles();

  return (

    <Paper>
      <section>
        <Container maxWidth="md">
          <Box py={6}>
            <Grid container spacing={4}>
              <Grid item xs={6} md={3}>
                <Mail color={theme.blue} fontSize="large" className={classes.icon} />
                <Typography variant="h6" component="h3" gutterBottom={true}>Accessibility</Typography>
                <Typography variant="body1" component="p">Ever worried that you won't be able to access your data in some places? Nevermore. With PiperNet your connection has no borders.</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <LinkedIn color="primary" fontSize="large" className={classes.icon} />
                <Typography variant="h6" component="h3" gutterBottom={true}>Secure</Typography>
                <Typography variant="body1" component="p">Have you ever heard about "not putting all of your eggs in one basket"? Well, with PiperNet you can actually put all your eggs in millions of baskets.</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <GitHub color="primary" fontSize="large" className={classes.icon} />
                <Typography variant="h6" component="h3" gutterBottom={true}>Speed</Typography>
                <Typography variant="body1" component="p">"Loading data" will soon be forgotten same way as floppy discs. With PiperNet algorithm, every data could travel faster than it was ever imaginable.</Typography>
              </Grid>
              <Grid item xs={6} md={3}>
                <GitHub color="primary" fontSize="large" className={classes.icon} />
                <Typography variant="h6" component="h3" gutterBottom={true}>Speed</Typography>
                <Typography variant="body1" component="p">"Loading data" will soon be forgotten same way as floppy discs. With PiperNet algorithm, every data could travel faster than it was ever imaginable.</Typography>
              </Grid>
            </Grid>
          </Box>
        </Container>
      </section>
    </Paper>
  )
}
