import React from 'react';
import MediaQuery from 'react-responsive';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import theme from '../theme/theme';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

import clsx from 'clsx';
import { useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import logo from './../images/logo.png';
import { Link } from 'react-router-dom';

import PersonAddIcon from '@material-ui/icons/PersonAdd';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';


const useStyles = makeStyles((theme) => ({
    root: {
        // background: 'white',
        // position: '-webkit-sticky',
        // position: 'sticky',
        // top: 20,
        // bottom: 20, 
        paddingTop: '10px',
        paddingBottom: '10px',
        color: 'black',
        backgroundColor: 'white',
        // backgroundColor: 'rgba(0, 0, 0, 0.25)',
        // opacity: '75%'
    },
}));

export default function App() {

    const classes = useStyles();

    return (
        <div>
            <MediaQuery maxWidth={theme.breakpoints.values.iPhone678Plus}>
                {/* <AppBar color="transparent" className={classes.root}>
                    <Toolbar>
                        <Grid container positionsticky="true" direction="row" justify="space-evenly" alignItems="center">
                            <Button color="paper" style={{fontSize: "16px"}}>Projects</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Experience</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Education</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>About</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Contact</Button>
                        </Grid>
                    </Toolbar>
                </AppBar> */}

<div className={classes.root}>
        <br/><br/>
        <CssBaseline />
        <AppBar color="default" positionsticky="true" className={clsx(classes.appBar, { [classes.appBarShift]: drawerOpen })} >
          <Toolbar>
            <Link to="/" variant="h5" color="inherit" className={classes.logo, classes.title}>
              <img src={logo} alt="CBA" width="100" />
            </Link>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="end"
              onClick={handleDrawerOpen}
              className={clsx(drawerOpen && classes.hide)}
            >
              <MenuIcon />
            </IconButton>
          </Toolbar>
        </AppBar>
        <Drawer className={classes.drawer} variant="persistent" anchor="right" open={drawerOpen} classes={{ paper: classes.drawerPaper }} >
          <div className={classes.drawerHeader}>
            <IconButton onClick={handleDrawerClose}>
              {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
            </IconButton>
          </div>

          <div align="center">
            <Typography variant="h6">Menu</Typography>
          </div>
          <List>
            <Link to="/login" style={{ textDecoration: 'none', color: 'inherit' }}>
              <ListItem button>
                <ListItemIcon>
                  <VerifiedUserIcon />
                </ListItemIcon>
                <ListItemText primary="Login" />
              </ListItem>
            </Link>

            <Link to="/register" style={{ textDecoration: 'none', color: 'inherit' }}>
              <ListItem button>
                <ListItemIcon>
                  <PersonAddIcon />
                </ListItemIcon>
                <ListItemText primary="Register" />
              </ListItem>
            </Link>
          </List>
        </Drawer>
      </div>

            </MediaQuery>

            <MediaQuery maxWidth={theme.breakpoints.values.iPad}>
            <AppBar color="transparent" className={classes.root}>
                    <Toolbar>
                        <Grid container positionsticky="true" direction="row" justify="space-evenly" alignItems="center">
                            <Button color="inherit" style={{fontSize: "16px"}}>Projects</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Experience</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Education</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>About</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Contact</Button>
                        </Grid>
                    </Toolbar>
                </AppBar>
            </MediaQuery>

            <MediaQuery minWidth={theme.breakpoints.values.iPad + 1}>
                <AppBar color="transparent" className={classes.root}>
                    <Toolbar>
                        <Grid container positionsticky="true" direction="row" justify="space-evenly" alignItems="center">
                            <Button color="inherit" style={{fontSize: "16px"}}>Projects</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Experience</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Education</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>About</Button>
                            <Button color="inherit" style={{fontSize: "16px"}}>Contact</Button>
                        </Grid>
                    </Toolbar>
                </AppBar>
            </MediaQuery>
        </div>
    )
}
