import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Accordion, Container } from '@material-ui/core';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import theme from '../theme/theme';
import service from '../services/CourseService';
import { useEffect } from 'react';

const useStyles = makeStyles(() => ({
  root: {
    maxWidth: '960px',

    justifyContent: "center",
    justifySelf: "center",
    justifyItems: "center",
    alignContent: "center",
    alignItems: "center",
    alignSelf: "center",

  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '90.00%',
    flexShrink: 0,
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
}));

export default function App() {

  const [expanded, setExpanded] = React.useState(false);
  const [courses, setCourses] = React.useState([]);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  useEffect(() => {
      service.getCourses().then(response => {
        setCourses(response.data);
      })
  }, []);

  const classes = useStyles();

  return (

    <section>
    <Container maxWidth="md">
        <Grid item xs={12} sm={12}>

          {courses.map(course => (
            <Accordion key={course._id} expanded={expanded === 'panel' + course._id} onChange={handleChange('panel' + course._id)}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />} aria-controls={"panelbh-content" + course._id} id={"panelbh-header" + course._id} >
                <Typography className={classes.heading}>{course.name}</Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Typography variant="body1" color="textSecondary">{course.description}</Typography>
              </AccordionDetails>
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <AccordionDetails>
                  {(() => {
                    if (course.technology != null && course.technology !== "") {
                      return (
                        <Typography variant="body2" color="textSecondary">Technologies Used:</Typography>
                      )
                    }
                  })()}
                  {course.technology.map(tech => (
                    <Typography key={tech} variant="body2" color="textSecondary">{tech}</Typography>
                  ))}
                </AccordionDetails>
                {(() => {
                  if (course.projectId != null && course.projectId !== "") {
                    return (
                    <AccordionDetails>
                      <Typography variant="body1" color="textSecondary" style={{paddingRight: "25px"}}>PROJECT ID</Typography>
                    </AccordionDetails>
                    )
                  }
                })()}
              </div>
            </Accordion>
          ))}

        </Grid>
      </Container>
    </section>

  )
}
