import React from 'react';
import MediaQuery from 'react-responsive';
import { CardMedia, Card, CardActionArea } from '@material-ui/core';

import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';

import video from '../images/Background.mov'
import cutout from '../images/Cutout.png';
import cutoutPhone from '../images/CutoutPhone.png';


const useStyles = makeStyles((theme) => ({
    div: {
        position: "relative",
      },
      backgroundImage: {
        width: "50vh",
        height: "auto",
      },
      cutoutImagePhone: {
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        width: "100%"
      },
      cutoutImageTablet: {
        position: "absolute",
        top: "70%",
        left: "44%",
        transform: "translate(-50%, -50%)",
        width: "90%"
      },
      cutoutImage: {
        position: "absolute",
        top: "70%",
        left: "35%",
        transform: "translate(-50%, -50%)",
        width: "70%"
      },
}));

export default function App() {

    const classes = useStyles();

    return (
        <div>
            <MediaQuery maxWidth={theme.breakpoints.values.iPhone678Plus}>
                <Card raised={true} >
                    <div className={classes.div} >
                        <CardActionArea style={{ height: "50vh" }}>
                            <CardMedia component='video' image={video} autoPlay loop muted style={{ height: "50vh", transform: "rotate(90deg)" }}/>
                        </CardActionArea>
                        <img src={cutoutPhone} alt="Holland Aucoin" className={classes.cutoutImagePhone} />
                    </div>
                </Card>
            </MediaQuery>
            <MediaQuery minWidth={theme.breakpoints.values.iPhone678Plus + 1} maxWidth={theme.breakpoints.values.iPadPro}>
            <div className={classes.div}>
                <Card raised={true}>
                        <CardActionArea>
                            <CardMedia component='video' image={video} autoPlay loop muted />
                        </CardActionArea>
                        <img src={cutout} alt="Holland Aucoin" className={classes.cutoutImageTablet} />
                </Card>
                </div>
            </MediaQuery>
            <MediaQuery minWidth={theme.breakpoints.values.iPadPro + 1} >
                <Card raised={true}>
                    <div className={classes.div}>
                        <CardActionArea>
                            <CardMedia component='video' image={video} autoPlay loop muted />
                        </CardActionArea>
                        <img src={cutout} alt="Holland Aucoin" className={classes.cutoutImage} />
                    </div>
                </Card>
            </MediaQuery>
        </div>
    )
}
