import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Grid, Container, Typography, Box, Avatar } from '@material-ui/core';
import service from '../services/RecommendationService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Recommendations
 * @Summary This component displays recommendations and information about the author
 */

const useStyles = makeStyles(() => ({
    container: {
        justifyContent: 'center'
      },
      avatar: {
        width: theme.spacing(10),
        height: theme.spacing(10),
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: theme.spacing(2)
      },
}));

export default function App() {

    // Creating variable for recommendations
    const [recommendations, setRecommendations] = React.useState([]);

    // Use useEffect method to call backend for course information and wait for response
    useEffect(() => {
        service.getRecommendations().then(response => {
            setRecommendations(response.data);
        })
    }, []);

    const classes = useStyles();

    return (

        <section>
            <Container maxWidth="md">
                <Box pt={8} pb={12} textAlign="center">
                    <Box mb={8}>
                        <Typography variant="h4" component="h2" gutterBottom={true}>The Pipers</Typography>
                        <Typography variant="subtitle1" color="textSecondary">We are the guys that made this whole thing possible.</Typography>
                    </Box>
                    <Grid container spacing={8} className={classes.container}>
                        <Grid item xs={12} sm={6}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1560298803-1d998f6b5249?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Richard Hendricks</Typography>
                            <Typography variant="body1" color={theme.palette.grey} component="span">Chief Executive Officer</Typography>
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <Typography variant="h6" component="h4" gutterBottom={true}>Richard Hendricks</Typography>
                            <Typography variant="body1" color="primary" component="span">Chief Executive Officer</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Monica Hall</Typography>
                            <Typography variant="body1" color="primary" component="span">Chief Financial Officer</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/flagged/photo-1570612861542-284f4c12e75f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Erlich Bachman</Typography>
                            <Typography variant="body1" color="primary" component="span">Marketing Manager</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1598627446792-5d89ab3e3540?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Nelson Bighetti</Typography>
                            <Typography variant="body1" color="primary" component="span">Majority Investor</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1598966739654-5e9a252d8c32?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Bertram Gilfoyle</Typography>
                            <Typography variant="body1" color="primary" component="span">Chief Systems Architect</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1561463385-0e5ea0ca925b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Dinesh Chugtai</Typography>
                            <Typography variant="body1" color="primary" component="span">Senior Programmer</Typography>
                        </Grid>
                        <Grid item xs={6} md={3}>
                            <Avatar alt="" src="https://images.unsplash.com/photo-1463453091185-61582044d556?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&ah=256&q=80" className={classes.avatar} />
                            <Typography variant="h6" component="h4" gutterBottom={true}>Jared Dunn</Typography>
                            <Typography variant="body1" color="primary" component="span">Chief Operating Officer</Typography>
                        </Grid>
                    </Grid>
                </Box>
            </Container>
        </section>

    )
}
