import React, { useEffect } from 'react';
import { Container, Box, Typography, Grid, Paper, Button } from '@material-ui/core';
import service from '../services/ProjectService';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';
import { useLocation } from 'react-router-dom';
import Carousel from 'react-material-ui-carousel'


/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Porject Details
 * @Summary This component displays the details of an individual project
 */

export default function App(props) {

  // Creating variables of skill lists to be filled be appropriate service call
  const [project, setProject] = React.useState();
  const [isLoading, setIsLoading] = React.useState(true);

  let location = useLocation();

  // Use useEffect method to call backend for each skill type and wait for response
  useEffect(() => {
    service.getProjectById(location.state.projectId).then(response => {
      setProject(response.data);
      setIsLoading(false);
    });
  }, []);


  let image = "";

  return (

      <section>
        { !isLoading && (
          <div>
            <Container maxWidth="md" >
              <Box py={6}>
                <div align="center" style={{paddingBottom: "25px"}}>
                  <img src={project.logo} style={{maxWidth: 600, maxHeight: 70}} alt="Logo" />
                </div>
                <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>
                  {project.introduction}
                </Typography>

                <Grid container style={{ justifyContent: "center" }}>
                  {/* Iterate through the skills using map, and use index variable to display each icon and title */}
                  {(project.technologies).map((tech, index) => (
                      image = "https://hollandaucoin-images.s3-us-west-1.amazonaws.com/technologies/".concat(tech),
                      <Box key={index} xs={6} sm={3} md={1} style={{ paddingLeft: "25px", paddingRight: "25px", paddingTop: "15px", paddingBottom: "25px", alignSelf: "center"}}>
                        <img style={{maxHeight: 80, maxWidth: 110}} src={image} alt="" />
                      </Box>
                    ))}
                </Grid>

                <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>
                  {project.objective}
                </Typography>
              </Box>
            </Container>

            <Paper>
                <div align="center" >
                  <Typography variant="h5" gutterBottom={true} style={{paddingTop: "40px", paddingBottom: "20px"}} >Design</Typography>
                </div>
              <Container maxWidth="md" style={{ paddingBottom: '30px' }}>
                <Carousel autoPlay={false} swipe={true}>

                  { Object.keys(project.designDescriptions).map( (key) => 
                      <div align="center">
                          <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>
                          {project.designDescriptions[key]}
                          </Typography>

                          <img style={{maxWidth: '100%', maxHeight: 500}} src={project.designImages[key]} alt={key} />
                      </div>
                    )
                  }
                </Carousel>
              </Container>
            </Paper>

            <div align="center" maxWidth="md">
                <Typography variant="h5" gutterBottom={true} style={{paddingTop: "40px", paddingBottom: "20px"}} >Final Product</Typography>
                <Typography variant="body1" color="textSecondary" paragraph={true} >
                { project.finalDescription }
                </Typography>
            </div>

            <Container maxWidth="md" style={{ paddingBottom: '30px' }}>
              <Carousel autoPlay={false} swipe={true}  >
                { Object.entries(project.finalImages).map( (image) => 

                    <div align="center">
                        <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>
                          <b>{image.key}</b>
                        </Typography>
                        <img style={{maxWidth: '100%', maxHeight: 500}} src={image.value} alt={image.value} />
                    </div>

                    )
                }
              </Carousel>
            </Container>

            <div align="center" maxWidth="md" style={{padding: 20}}>
              <Typography variant="body1" color="textSecondary" paragraph={true} >
                To view more details about this project, please visit my <a target="_blank" href={project.gitHub} rel="noreferrer">GitHub.</a>
              </Typography>
            </div>

          </div>
          )
        }
      </section>
  )
}
