import React, { useEffect } from 'react';
import { Container, Box, Typography, Grid } from '@material-ui/core';
import service from '../services/ProjectService';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';
import { useLocation } from 'react-router-dom';


/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Porject Details
 * @Summary This component displays the details of an individual project
 */

export default function App(props) {

  // Creating variables of skill lists to be filled be appropriate service call
  const [project, setProject] = React.useState();
  const [isLoading, setIsLoading] = React.useState(true);

  let location = useLocation();

  // Use useEffect method to call backend for each skill type and wait for response
  useEffect(() => {
    service.getProjectById(location.state.projectId).then(response => {
      setProject(response.data);
      setIsLoading(false);
    });
  }, []);


  let image = "";

  return (

      <section>
        <Container maxWidth="md" >
          <Box py={6}>
            <div align="center" style={{paddingBottom: "25px"}}>
              <Typography variant="h5" gutterBottom={true}>Logo</Typography>
            </div>
            <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>
              Alias sunt voluptas ratione modi dolore nostrum debitis nihil. Nemo, ratione repellat quia doloremque perferendis fuga cumque ex corporis laborum distinctio dolorum deserunt voluptates ea architecto ab, esse omnis quas provident. Maiores sed ipsam eos quis.
              Alias sunt voluptas ratione modi dolore nostrum debitis nihil. Nemo, ratione repellat quia doloremque perferendis fuga cumque ex corporis laborum distinctio dolorum deserunt voluptates ea architecto ab, esse omnis quas provident. Maiores sed ipsam eos quis.
            </Typography>

            <Grid container style={{ justifyContent: "space-around" }} alignItems="center">
              {/* Iterate through the skills using map, and use index variable to display each icon and title */}

              { !isLoading && 

                (project.technology).map((tech, index) => (
                  image = "https://hollandaucoin-images.s3-us-west-1.amazonaws.com/technologies/".concat(tech),
                  <Grid key={index} item xs={6} sm={3} md={1} style={{textAlign: "center", padding: "15px"}}>
                     <img style={{maxHeight: 80, maxWidth: 110}} src={image} alt="" />
                  </Grid>
                ))

              }

            </Grid>
          </Box>
        </Container>
      </section>

  )
}
