import React, { useEffect, Component } from 'react';
import { Container, Box, Typography, Grid, Card, CardMedia } from '@material-ui/core';
import service from '../services/ProjectService';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Porject Details
 * @Summary This component displays the details of an individual project
 */

const useStyles = makeStyles(() => ({


}));

export default function App() {

  // Creating variables of skill lists to be filled be appropriate service call
  const [project, setProject] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  // Use useEffect method to call backend for each skill type and wait for response
  useEffect(() => {
    service.getProjects().then(response => {
      setProject(response.data[2]);
      setIsLoading(false);
    })
  }, []);

  // let tech = project.technology;
  alert(project);

  const classes = useStyles();

  let image = "";

  return (

      <section>
        {isLoading && <p>Content is being loaded</p>}
        <Container maxWidth="md" >
          <Box py={6}>
            <div align="center" style={{paddingBottom: "25px"}}>
              <Typography variant="h5" gutterBottom={true}>Logo</Typography>
            </div>
            <Typography variant="body1" color="textSecondary" paragraph={true} style={{textAlign: "justify"}}>Alias sunt voluptas ratione modi dolore nostrum debitis nihil. Nemo, ratione repellat quia doloremque perferendis fuga cumque ex corporis laborum distinctio dolorum deserunt voluptates ea architecto ab, esse omnis quas provident. Maiores sed ipsam eos quis.</Typography>

            <Grid container spacing={4} style={{ justifyContent: "space-between" }}>
              {/* Iterate through the skills using map, and use index variable to display each icon and title */}

                {(project.technology).map((tech, index) => (
                  image = "https://hollandaucoin-images.s3-us-west-1.amazonaws.com/technologies/".concat(tech),
                  <Grid key={project.technology} item xs={6} sm={3} md={1} style={{textAlign: "center"}}>
                     <img style={{width: 70}} src={image} alt="" />
                  </Grid>
                ))

              }

            </Grid>
          </Box>
        </Container>
      </section>

  )
}
