import React from 'react';
import MediaQuery from 'react-responsive';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import theme from '../theme/theme';
import image from '../images/HeroBanner.jpg';
import cutout from '../images/Cutout.png';


const useStyles = makeStyles((theme) => ({
    div: {
        position: "relative",
      },
      image: {
        width: "100%",
      },
      button: {
        color: "white",
        borderColor: "white",
        [theme.breakpoints.down('xs')]: {
          fontSize: "10px",
          top: "65%",
        },
        [theme.breakpoints.up('sm')]: {
          fontSize: "15px",
        },
        [theme.breakpoints.up('md')]: {
          fontSize: "20px",
        },
        [theme.breakpoints.up('lg')]: {
          fontSize: "25px",
        },
        position: "absolute",
        top: "60%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        "&:hover": {
          background: "rgba(60, 60, 60, 0.25)",
        },
      },
      title: {
        color: "white",
        [theme.breakpoints.down('xs')]: {
          fontSize: "30px",
          top: "40%",
        },
        [theme.breakpoints.up('sm')]: {
          fontSize: "38px",
        },
        [theme.breakpoints.up('md')]: {
          fontSize: "50px",
        },
        [theme.breakpoints.up('lg')]: {
          fontSize: "65px",
        },
        fontWeight: "bold",
        position: "absolute",
        top: "45%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        width: "94%"
      }
}));

export default function App() {

    const classes = useStyles();

    return (
        <div>
            <MediaQuery maxWidth={theme.breakpoints.values.iPhone678Plus}>
                {/* <div align="left">
                    <img src={logo} alt="Website Logo" width="80%" />

                </div> */}
            </MediaQuery>

            <MediaQuery maxWidth={theme.breakpoints.values.iPad}>
                {/* <div align="left">
                    <img src={logo} alt="Website Logo" width="80%" />

                </div> */}
            </MediaQuery>

            <MediaQuery minWidth={theme.breakpoints.values.iPad + 1}>

                <div className={classes.div}>
                    <img src={image} alt="" className={classes.image} />
                    <img src={cutout} alt="" className={classes.title} gutterBottom={true}/>

                </div>
            </MediaQuery>

        </div>
    )
}
