import React, { useEffect } from 'react';
import { AppBar, Tabs, Tab, Typography, Box, Container, Grid, Card, CardActionArea, CardMedia, CardHeader } from '@material-ui/core';
import service from '../services/ProjectService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';

import PropTypes from 'prop-types';
import SwipeableViews from 'react-swipeable-views';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Projects Tabular
 * @Summary This component displays the tabular view of all projects that is shown on the projects page
 */

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme.palette.background.paper,
    },
    tabs: {
        minWidth: 50,
        ['@media (min-width:600px)']: { // eslint-disable-line no-useless-computed-key
            minWidth: 50
        }
    },
    media: {
        height: '256px'
    },
}));

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`full-width-tabpanel-${index}`}
            aria-labelledby={`full-width-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `full-width-tab-${index}`,
        'aria-controls': `full-width-tabpanel-${index}`,
    };
}


export default function App() {

    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    const handleChangeIndex = (index) => {
        setValue(index);
    };

    // Creating variables of skill lists to be filled be appropriate service call
    const [completedProjects, setCompletedProjects] = React.useState([]);
    const [currentProjects, setCurrentProjects] = React.useState([]);

    // Use useEffect method to call backend for each project type and wait for response
    useEffect(() => {
        service.getProjectsByType("Completed").then(response => {
            setCompletedProjects(response.data);
        })
        service.getProjectsByType("Current").then(response => {
            setCurrentProjects(response.data);
        })
    }, []);

    // Iterate through an array of projects and turn it into its own array based on technology
    function getProjectOfLanguage(language) {
        let projectsByLanguage;

        completedProjects.map((project, index) => (

            project.languages.includes(language) ? projectsByLanguage[index] = project : null
        ))

        return projectsByLanguage;
    }

    // Set array of projects based on language by calling method
    let javaProjects = getProjectOfLanguage("Java");
    let cProjects = getProjectOfLanguage("C#");
    let phpProjects = getProjectOfLanguage("PHP");
    let javascriptProjects = getProjectOfLanguage("JavaScript");


    return (

        <section>
            <Box pt={8} pb={10}>
                <Container maxWidth="md">
                    <Box textAlign="center" mb={5}>
                        <Typography variant="h5" component="h2" gutterBottom={true}>See my work.</Typography>
                        <Typography variant="subtitle1" color="textSecondary">Completed Projects</Typography>
                    </Box>
                </Container>
                <Container maxWidth="md">
                    <AppBar position="static" color="default">
                        <Tabs
                            value={value}
                            onChange={handleChange}
                            indicatorColor="primary"
                            textColor="primary"
                            variant="fullWidth"
                            aria-label="full width tabs example"
                        >
                            <Tab label="All" {...a11yProps(0)} className={classes.tabs} />
                            <Tab label="Java" {...a11yProps(1)} className={classes.tabs} />
                            <Tab label="C#" {...a11yProps(2)} className={classes.tabs} />
                            <Tab label="PHP" {...a11yProps(3)} className={classes.tabs} />
                            <Tab label="JS" {...a11yProps(4)} className={classes.tabs} />
                        </Tabs>
                    </AppBar>
                    <SwipeableViews
                        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
                        index={value}
                        onChangeIndex={handleChangeIndex}
                    >
                        <TabPanel value={value} index={0} dir={theme.direction}>
                            <Box textAlign="center">
                                <Grid container spacing={3}>

                                    {completedProjects.map((project, id) => (
                                        <Grid item xs={12} sm={6} md={4}>
                                            <Card>
                                                <CardActionArea href="#">
                                                    <CardMedia component="img" className={classes.media} image="https://images.unsplash.com/photo-1522071820081-009f0129c71c?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" />
                                                    <CardHeader subheader={project.name} sub titleTypographyProps={{ gutterBottom: false }} />
                                                </CardActionArea>
                                            </Card>
                                        </Grid>
                                    ))
                                    }

                                </Grid>
                            </Box>
                        </TabPanel>
                        <TabPanel value={value} index={1} dir={theme.direction}>
                            <Box textAlign="center">
                                <Grid container spacing={3}>

                                    {javaProjects.map((project, id) => (
                                        <Grid item xs={12} sm={6} md={4}>
                                            <Card>
                                                <CardActionArea href="#">
                                                    <CardMedia component="img" className={classes.media} image="https://images.unsplash.com/photo-1522071820081-009f0129c71c?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" />
                                                    <CardHeader subheader={project.name} sub titleTypographyProps={{ gutterBottom: false }} />
                                                </CardActionArea>
                                            </Card>
                                        </Grid>
                                    ))
                                    }

                                </Grid>
                            </Box>
                        </TabPanel>
                        <TabPanel value={value} index={2} dir={theme.direction}>
                            <Box textAlign="center">
                                <Grid container spacing={3}>

                                    {cProjects.map((project, id) => (
                                        <Grid item xs={12} sm={6} md={4}>
                                            <Card>
