import React from 'react';
import { Container, Box, Typography, Grid, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import theme from '../theme/theme';
import me from '../images/AboutMe.jpg';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Biography
 * @Summary This component displays a biography and picture of myself
 */

const useStyles = makeStyles(() => ({
      contentBox: {
        maxWidth: theme.breakpoints.values['md'],
        paddingTop: theme.spacing(4),
        paddingBottom: theme.spacing(4),
        marginLeft: 'auto',
        marginRight: 'auto',
        textAlign: 'left',
        // [theme.breakpoints.up('lg')]: {
        //   maxWidth: theme.breakpoints.values['lg'] / 2,
        //   maxHeight: 512,
        //   paddingTop: theme.spacing(16),
        //   paddingBottom: theme.spacing(16),
        //   marginRight: 0,
        //   textAlign: 'left',
        // }
      },
      img: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        objectFit: 'cover',
        height: 512,
        width: '100%'
      },
      button: {
        color: theme.palette.text.black,
        backgroundColor: "transparent",
        height: "50px",
      },
      halfLg: {
        [theme.breakpoints.up('lg')]: {
          maxWidth: theme.breakpoints.values['lg'] / 2
        }
      },
      firstBox: {
        [theme.breakpoints.up('lg')]: {
          marginLeft: 'auto',
          paddingRight: theme.spacing(6)
        }
      },
      fullHeightImage: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        objectFit: 'cover',
        height: 768,
        width: '100%'
      },
}));

export default function App() {

  const classes = useStyles();


  return (

    <section>
    {/* <Grid container>
        <Grid item sm={12} md={5}>
        <Box position="relative" height={512}>
            <img className={classes.img} src={me} alt="" />
        </Box>
        </Grid>
        <Grid item sm={12} md={7}>
        <Box className={classes.contentBox}>
            <Container>
            <Typography variant="h4" component="h2" gutterBottom={true}>Get to know me.</Typography>
            <Typography color="textSecondary" paragraph={true}>The PiperNet is on it's way to revolutionize every smartphone, PC, and smart-fridge near you.</Typography>
            </Container>
        </Box>
        </Grid>
    </Grid> */}

    <Grid container>
    {/* Image that adjusts based on screen size */}
        <Grid item xs={12} md={6}>
          <Box position="relative" height={768}>
            <img className={classes.fullHeightImage} src={me} alt="" />
          </Box>
        </Grid>
        <Grid item xs={12} md={6}>
          <Box pt={10} pb={8} display="flex" className={[classes.halfLg, classes.firstBox]}>
            <Container>
              <Box mb={4}>
                <Typography variant="h4" component="h2" gutterBottom={true}>Let's get connected.</Typography>
                <Typography variant="subtitle1" color="textSecondary" paragraph={true}>Determined and passionate software developer <b>actively seeking new opportunities</b> and would love to hear from you.</Typography>
              </Box>
            </Container>
          </Box>
        </Grid>
      </Grid>
    </section>
  )
}
