import React, { useEffect } from 'react';
import MediaQuery from 'react-responsive';
import { AppBar, Tabs, Tab, Typography, Box, Container } from '@material-ui/core';
import service from '../services/ProjectService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';

import PropTypes from 'prop-types';
import SwipeableViews from 'react-swipeable-views';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Projects Tabular
 * @Summary This component displays the tabular view of all projects that is shown on the projects page
 */

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`full-width-tabpanel-${index}`}
            aria-labelledby={`full-width-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box p={3}>
                    <Typography>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `full-width-tab-${index}`,
        'aria-controls': `full-width-tabpanel-${index}`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme.palette.background.paper,
    },
}));


export default function App() {

    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    const handleChangeIndex = (index) => {
        setValue(index);
    };

    return (
        <div className={classes.root} maxWidth="md">
            <Container maxWidth="md">
            <Typography variant="h5" component="h2" gutterBottom={true}>See my work.</Typography>
                <AppBar position="static" color="default">
                    <Tabs
                        value={value}
                        onChange={handleChange}
                        indicatorColor="primary"
                        textColor="primary"
                        variant="fullWidth"
                        aria-label="full width tabs example"
                        style={{minWidth: "100px"}}
                    >
                       <MediaQuery maxWidth={theme.breakpoints.values.otherPhone}>
                        <Tab label="All" {...a11yProps(0)} />
                        <Tab label="Java" {...a11yProps(1)} />
                        <Tab label="C#" {...a11yProps(2)} />
                        <Tab label="PHP" {...a11yProps(3)} />
                            <Tab label="JS" {...a11yProps(4)} />
                        </MediaQuery>
                        <MediaQuery minWidth={theme.breakpoints.values.otherPhone + 1} >
                            <Tab label="JavaScript" {...a11yProps(4)} />
                        </MediaQuery>
                    </Tabs>
                </AppBar>
                <SwipeableViews
                    axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
                    index={value}
                    onChangeIndex={handleChangeIndex}
                >
                    <TabPanel value={value} index={0} dir={theme.direction}>
                        Item One
                    </TabPanel>
                    <TabPanel value={value} index={1} dir={theme.direction}>
                        Item Two
                    </TabPanel>
                    <TabPanel value={value} index={2} dir={theme.direction}>
                        Item Three
                    </TabPanel>
                </SwipeableViews>
            </Container>
        </div>

    )
}
